mapper.select:
select
from LSTFB where 1=1
and ID = #{id,jdbcType=VARCHAR}

mapper.insert:
insert into LSTFB (ID, PERSONID, APPLYDATE,
APPLYPOLICE, PRSFULLNAME, SEX,
AGE, PRSRELATION, APPLYREASON
)
values (#{id,jdbcType=VARCHAR}, #{personid,jdbcType=VARCHAR},
#{applydate,jdbcType=TIMESTAMP},
#{applypolice,jdbcType=VARCHAR}, #{prsfullname,jdbcType=VARCHAR}, #{sex,jdbcType=VARCHAR},
#{age,jdbcType=VARCHAR}, #{prsrelation,jdbcType=VARCHAR},
#{applyreason,jdbcType=VARCHAR}
)
